  let frame_rate = 60;
  let sleep_ms = time::Duration::from_secs(1) / frame_rate;

  loop {
    let start = time::Instant::now();
    game_logic(); // ã¡ã‚‡ã£ã¨æ™‚é–“ãŒã‹ã‹ã‚‹
    let elapsed = time::Instant::now() - start;
